def verify_results():
    """Run all functions and verify against known answers"""
    import euler
    
    print("Verifying ALL Project Euler solutions...")
    print("=" * 60)
    
    all_correct = True
    total_tests = 0
    passed_tests = 0
    failed_tests = []
    
    for func_name, expected_answer in CORRECT_ANSWERS.items():
        total_tests += 1
        
        try:
            # Get the function from the euler module
            func = getattr(euler, func_name)
            
            # Run the function with appropriate arguments
            if func_name in FUNCTION_ARGS:
                result = func(*FUNCTION_ARGS[func_name])
            else:
                result = func()
            
            # Check if result matches expected answer
            if result == expected_answer:
                print(f"✅ {func_name:30} {result:15} (CORRECT)")
                passed_tests += 1
            else:
                print(f"❌ {func_name:30} {result:15} (EXPECTED: {expected_answer})")
                failed_tests.append(func_name)
                all_correct = False
                
        except Exception as e:
            print(f"❌ {func_name:30} ERROR - {str(e)[:50]}...")
            failed_tests.append(func_name)
            all_correct = False
    
    print("=" * 60)
    print(f"Results: {passed_tests}/{total_tests} tests passed")
    
    if all_correct:
        print("🎉 All solutions are correct!")
    else:
        print(f"⚠️  {len(failed_tests)} solutions failed:")
        for failed in failed_tests:
            print(f"   - {failed}")
    
    return all_correct

if __name__ == "__main__":
    verify_results() 